import { useContext, useEffect, useState } from 'react';
import {useLocation, Outlet, Navigate} from 'react-router-dom';
import { AuthContext } from '../contexts';
import {getMe} from '../Model/LoginAPI'
import { getAuthToken } from '../Model/utils'



const ProtectedRoutes = () => {
    // console.log("ProtectedRoutes");
    // // const {user, setUser} = useContext(AuthContext)
    // // const [login, setLogin] = useState(false);
    const location = useLocation();
    // // console.log("userloggedin "+login);
    // const ANS = ()=>{
    //     if(getAuthToken()){

    //         return <Outlet/>;
    //     }else{
    //         return <Navigate to='/login' replace state={{ from: location }} />;
    //     }
    // }


    // return <ANS/>;

    const [auth, setAuth] = useState(false);
    const [isTokenValidated, setIsTokenValidated] = useState(false);
    let token = getAuthToken();

    useEffect(()=>{
        let token = getAuthToken();
        console.log("token");
        if(token){
            getMe()
            .then((json)=>{
                if(json.success){
                    setAuth(true);
                }
            })
            .catch((err) => {
                setAuth(false);
                localStorage.removeItem("token");
              })
            .then(() => setIsTokenValidated(true));
        }else{
            setIsTokenValidated(true);
        }
    },[])
    if (!isTokenValidated) return <div>logining</div>;

    return (auth ? <Outlet/> : <Navigate to='/login' replace state={{ from: location }} />);
}

export default ProtectedRoutes
