import React, { useContext, useState } from 'react'
import { json, useNavigate } from "react-router-dom";
import 'semantic-ui-css/semantic.min.css'
import { Button, Form, Grid, Segment, Header, Icon} from 'semantic-ui-react'
import { setAuthToken } from '../Model/utils'
import {getMe, login} from '../Model/LoginAPI'
import { AuthContext } from '../contexts';
const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [errorMessage, setErrorMessage] = useState();
    const {setUser} = useContext(AuthContext);
    const history = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        login(username, password).then((data)=>{
           if (data.ok === 0){
            return setErrorMessage(data.message);
           } 
           setAuthToken(data.token);
           getMe()
           .then((res)=>{
            if(data.ok !== 1){
                setAuthToken(null)
                setErrorMessage(res.toString());
            }
            errorLogin();
            return res;
          }).then((res)=>{
              console.log(res.success);
              if(res.success === 'ok'){
                history("/list");
              }
           });
        });
    };

    const handleUsername = (e) => {
        setUsername(e.target.value);
    }

    const handlePassword = (e) => {
        setPassword(e.target.value);
    }

    const errorLogin = (e)=>{
        setError("error");
    }

    return (
      <div>
        <Segment placeholder>
          <Header as='h1' icon textAlign='center'>
              <Icon name='user' circular/>
              <Header.Content>Login todo-List</Header.Content>
          </Header>
        <Grid columns={1} relaxed='very' stackable>
          <Grid.Column onSubmit = {handleSubmit}>
            <Form>
              <Form.Input
                icon='user'
                iconPosition='left'
                label='Username'
                placeholder='Username'
                value={username}
                onChange={handleUsername}
              />

              <Form.Input
              inline
                icon='lock'
                iconPosition='left'
                label='Password'
                type='password'
                value={password}
                onChange={handlePassword}
                error={{
                  content: 'Please enter a valid email address',
                  pointing: 'left',
                }}
              />
                  <Segment.Inline>
                    <Button primary>Clear Query</Button>
                    <Button>Add Document</Button>
                  </Segment.Inline>
              <Button content='Login' primary />
            </Form>
          </Grid.Column>
        </Grid>
      </Segment>

        <Segment placeholder>
          <Header as='h1' icon textAlign='center'>
              <Icon name='user' circular/>
              <Header.Content>Login todo-List</Header.Content>
          </Header>
          <Grid columns={1} relaxed='very' stackable>
          <Grid.Column onSubmit = {handleSubmit}>
        <Segment.Inline>
          <Button primary>Clear Query</Button>
          <Button>Add Document</Button>
        </Segment.Inline>
        </Grid.Column>
        </Grid>
      </Segment>
      </div>
    )
}

export default Login
