import React, { useContext, useState } from 'react'
import { useNavigate } from "react-router-dom";
import 'semantic-ui-css/semantic.min.css'
import { Button, Form, Grid, Segment, Header, Icon} from 'semantic-ui-react'
import { setAuthToken } from './utils'
import {getMe, login} from './WebAPI'
import { AuthContext } from './contexts';
const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [errorMessage, setErrorMessage] = useState();
    const {setUser} = useContext(AuthContext);
    const history = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        login(username, password).then((data)=>{
            alert(data.ok);
            alert(data.status);
           if (data.ok === 0){
            return setErrorMessage(data.message);
           } 
           setAuthToken(data.token);
           getMe().then((res)=>{
            // if(data.ok !== 1){
            //     setAuthToken(null)
            //     setErrorMessage(res.toString());
            // }
           });

           history("/list");
        });
    };

    const handleUsername = (e) => {
        setUsername(e.target.value);
    }

    const handlePassword = (e) => {
        setPassword(e.target.value);
    }


    return (
    <div>
        <Header as='h1' icon textAlign='center'>
            <Icon name='user' circular/>
            <Header.Content>Login todo-List</Header.Content>
        </Header>
        <Segment placeholder>
        <Grid columns={1} relaxed='very' stackable>
          <Grid.Column onSubmit = {handleSubmit}>
            <Form>
              <Form.Input
                icon='user'
                iconPosition='left'
                label='Username'
                placeholder='Username'
                value={username}
                onChange={handleUsername}
              />
              <Form.Input
                icon='lock'
                iconPosition='left'
                label='Password'
                type='password'
                value={password}
                onChange={handlePassword}
              />

              <Button content='Login' primary />
            </Form>
          </Grid.Column>
        </Grid>
      </Segment>
    </div>
    )
}

export default Login
