import { useContext, useEffect, useState } from 'react';
import {useLocation, Outlet, Navigate} from 'react-router-dom';
import { AuthContext } from '../contexts';
import {getMe} from '../Model/LoginAPI'
import { getAuthToken } from '../Model/utils'


const ProtectedRoutes = () => {
    console.log("ProtectedRoutes");
    // const {user, setUser} = useContext(AuthContext)
    const {login, setLogin} = useState(false);
    const location = useLocation();
    console.log("userloggedin "+login);

    // if(getAuthToken()){
    //     console.log("GA"+getAuthToken());
    //     getMe()
    //     .then((res)=>{
    //         console.log("123");
    //         if(res.success){
    //             setUser(true);
    //         }
    //     })
    //     .finally(()=>{

    //     });

    // }else{
    //     return <Navigate to='/login' replace state={{ from: location }} />
    // }
    useEffect(()=>{
        if(getAuthToken()){
            getMe()
            .then((res)=>{
                if(res.success){

                    setLogin(true);
                    console.log("321"+login);
                }
            })
        }else{
            setLogin(false);
            console.log("4141"+login);
        }
    },[]);
    return(
        <div>
        {login}?<Outlet/>:<Navigate to='/login' replace state={{ from: location }} />
        </div>
    );

}

export default ProtectedRoutes
