import React, { useContext, useState } from 'react'
import { json, useNavigate } from "react-router-dom";
import 'semantic-ui-css/semantic.min.css'
import { Button, Form, Container, Segment, Header, Icon} from 'semantic-ui-react'
import { setAuthToken } from '../Model/utils'
import {getMe, login} from '../Model/LoginAPI'
import { AuthContext } from '../contexts';
const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [errorMessage, setErrorMessage] = useState();
    const {setUser} = useContext(AuthContext);
    const history = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        login(username, password).then((data)=>{
           if (data.ok === 0){
            return setErrorMessage(data.message);
           } 
           setAuthToken(data.token);
           getMe()
           .then((res)=>{
            if(data.ok !== 1){
                setAuthToken(null)
                setErrorMessage(res.toString());
            }
            errorLogin();
            return res;
          }).then((res)=>{
              console.log(res.success);
              if(res.success === 'ok'){
                history("/list");
              }
           });
        });
    };

    const handleUsername = (e) => {
        setUsername(e.target.value);
    }

    const handlePassword = (e) => {
        setPassword(e.target.value);
    }

    const errorLogin = (e)=>{
        setError("error");
    }

    return (
      <div>
          <Header as='h1' icon textAlign='center'>
              <Icon name='user' circular/>
              <Header.Content>Login todo-List</Header.Content>
          </Header>
            <Form widths='equal'onSubmit = {handleSubmit}>
              <Form.Input
                inline
                icon='user'
                iconPosition='left'
                label='Username'
                placeholder='Username'
                value={username}
                onChange={handleUsername}
              />

              <Form.Input
                inline
                icon='lock'
                iconPosition='left'
                label='Password'
                type='password'
                value={password}
                onChange={handlePassword}
                // error={{
                //   content: 'Please enter a valid email address',
                //   pointing: 'left',
                // }}
              />
              <Button content='Login' primary />
            </Form> 
      </div>
    )
}

export default Login
