export const login = (Account, Password) => {
    return fetch(`http://localhost:8080/login`,{
        method : "POST",
        headers:{
            "content-type": "application/json",
        },
        body: JSON.stringify({
            Account,
            Password,
        }),
    })
    .then((res)=>res.json())
    .catch((err)=>{
        console.log("lgon error")
    })
};

export const getMe = () => {
    const token = localStorage.getItem("token");
    return fetch(`http://localhost:8080/user`,{
        method:"GET",
        headers:{
            Authorization: `${token}`,
        },
    })
    .then((res)=>res.json);
}
