import React, { useContext, useEffect, useState } from 'react'
import { useLocation } from "react-router-dom";
import { Message, Button, Card, Container, Grid, Header, Icon} from 'semantic-ui-react'
import {getList} from '../Model/LoginAPI'
import 'semantic-ui-css/semantic.min.css'


function User(props){
    const username = props.user;
    if(username){
        return(                 
            <Header.Subheader>
                Hello {username} Welcome Back to Your TodoList {'>'}_{'<'}!
            </Header.Subheader>
        );
    }
    return
}
function TodoList(props){
    const List = props.Lists.allList;


    return(<Card>
        <Card.Content>
            <Button icon floated='right'>
                <Icon name='delete' />
            </Button>
            <Card.Header>Steve Sanders</Card.Header>
            <Card.Meta>Friends of Elliot</Card.Meta>
            <Card.Description>
            Steve wants to add you to the group <strong>best friends</strong>
            </Card.Description>
         </Card.Content>
        <Card.Content extra>
            <div className='ui two buttons'>
                <Button basic color='green'>
                    Approve
                </Button>
                <Button basic color='yellow'>
                    Decline
                </Button>

            </div>
        </Card.Content>
        <p value="123"></p>
  </Card>);
}
function AllLists(props){
    const propslen = props.allList.length;
    let groupArray = [];
    if(!props.allList.length) return null;
    // const group = props.allList.map((_,index)=>{
    //     if(!(index%3)){
    //         groupArray[index/3]= props.allList.slice(index,index+3);
    //     }
    //     return
    // });
    console.log(props.allList);

    return(
        <div>
        <h1>Personal Information</h1>
        <ul className="profile-list">
          {
            props.allList.map ((content,index) =>{
                if(!(index%3)){
                    return(
                        <li key ={content.ID}>
                        <span><strong>ID:</strong> {content.ID}</span>
                        <span><strong>First Name:</strong> {content.UserName}</span>
                        <span><strong>List:</strong> {content.List}</span>
                        </li>
                    );
                }
            })
          }
        </ul>
    </div>
        // <Card.Group>
        //    <TodoList Lists={props}/> 
        // </Card.Group>
    );
}

const List = () => {
    // const location = useLocation()

    const [username,setusername] = useState("");
    const [allList, setAllList] = useState([]);

    useEffect(()=>{
        getList()
        .then((res)=>{
            setusername(res.username);
            setAllList(res.AllList);
        });
    },[]);

    return(
        <Container>
            <Header as='h1' icon textAlign='center'>
                <Icon name='unordered list' circular />
                <Header.Content>List</Header.Content>
                <User user={username}/>
            </Header>
            <AllLists allList={allList}/>

        </Container>
    )
}
export default List
